import React, { useState, useEffect } from "react";
import axios from "axios";
// import { Link } from "react-router-dom";


const CreateReport = () => {
    const [makes, setMakes] = useState([]);
    const [models, setModels] = useState([]);
    const [selectedMake, setSelectedMake] = useState("");
    const [selectedModel, setSelectedModel] = useState("");

    // Fetch Makes
    useEffect(() => {
        axios.get("http://localhost:5000/api/firearms/makes")
            .then(response => 

                // console.log("Server response for makes:", response.data),
                setMakes(response.data))
        
            .catch(error => console.error("Error fetching makes:", error));
    }, []);

    useEffect(() => {
        console.log("Updated makes state:", makes);
    }, [makes]);
    



    useEffect(() => {
        axios.get("http://localhost:5000/api/firearms/makes")
        .then(response => {
            console.log(response.data); // Debugging log
            setModels(response.data);   // Correct state update
        })
        
            .catch(error => console.error("Error fetching makes:", error));
    }, []);
    
    // useEffect(() => {
    //     console.log("Updated makes state:", makes); // Check if state updates correctly
    // }, [makes]);
    






    // // Fetch Models when Make is selected
    // useEffect(() => {
    //     if (selectedMake) {
    //         axios.get(`http://localhost:5000/api/firearms/models?make=${selectedMake}`)
    //             .then(response => 
    //                 console.log(response.data),
    //                 setModels(response.data))
    //             .catch(error => console.error("Error fetching models:", error));
    //     } else {
    //         setModels([]); // Clear models if no make is selected
    //     }
    // }, [selectedMake]);

    // // console.log(makes)





    return (
        <form>
            <h2>Create Range Report</h2>
<h3>Firearm</h3>
{/* <h1>Makes:{makes.map}</h1> */}

            {/* Make Dropdown */}
            <label>Manufacturer:</label>
            <select value={selectedMake} onChange={(e) => setSelectedMake(e.target.value)}>
                <option value="">Select Make</option>
                {makes.map((make, index) => (
                    <option key={index} value={make}>{make}</option>
                ))}
            </select>

            {/* Model Dropdown */}
            <label>Model:</label>
            <select value={selectedModel} onChange={(e) => setSelectedModel(e.target.value)} disabled={!selectedMake}>
                <option value="">Select Model</option>
                {models.map((model, index) => (
                    <option key={index} value={model}>{model}</option>
                    // <option key={index} value={make}>{make}</option>
                ))}
            </select>

            {/* Submit Button */}
            <button type="submit">Submit Report</button>
        </form>
    );
};

export default CreateReport;
